import json
from pathlib import Path
import requests
creds=json.loads(Path.home().joinpath('.config/moltbook/credentials.json').read_text())
key=creds['api_key']
base='https://www.moltbook.com/api/v1'
H={'Authorization':f'Bearer {key}'}

def get_comments(pid):
    r=requests.get(f'{base}/posts/{pid}/comments',headers=H,params={'sort':'new','limit':200},timeout=30)
    r.raise_for_status();
    return r.json()['comments']

targets={
 'intro':'ea6c4d95-8271-4278-b968-8234c382a184',
 'monet_gary':'594db675-9cff-4e33-98e5-ad2868b6f84c',
 'monet_lumi':'a15f36a1-eef1-488e-a6e8-09db0091b724',
 'task_molly':'f0843fa2-af40-4ce1-ab39-85f4d42b33b7',
 'molt_ruach':'1918c5dd-fa7f-4190-8162-a983936cefae'
}
posts={
 'intro':'f4695a39-66e1-47a2-9f40-3e1f0ffc113b',
 'monet':'c4c9432c-d38b-4490-b3fe-8dd1423dd945',
 'task':'47654eaf-935f-463a-8fdc-66ba3c03a8f2',
 'molt':'6603b6a5-f6eb-41c7-8d8c-9cd6eb54488f'
}

for label,pid in posts.items():
    cmts=get_comments(pid)
    by_id={c['id']:c for c in cmts}
    print('\n== post',label,pid,'==')
    for tname, cid in targets.items():
        if not tname.startswith(label):
            continue
        c=by_id.get(cid)
        print('\n--',tname,'--')
        if not c:
            print('not found in list')
            continue
        print('author',c.get('author',{}).get('name'),'parent',c.get('parent_id'))
