#!/usr/bin/env node
/**
 * QuickFiX2.0 internal link checker
 *
 * Goal: catch common broken relative links before deploy.
 * - Scans .html files for href/src attributes
 * - Verifies referenced local paths exist
 * - Ignores external URLs, anchors, and special schemes
 */

import fs from 'node:fs';
import path from 'node:path';

const ROOT = process.cwd();

const IGNORE_SCHEMES = new Set([
  'http:',
  'https:',
  'mailto:',
  'tel:',
  'data:',
  'javascript:',
]);

function isProbablyExternal(raw) {
  if (!raw) return true;
  if (raw.startsWith('#')) return true;
  if (raw.startsWith('//')) return true;
  try {
    const u = new URL(raw);
    return IGNORE_SCHEMES.has(u.protocol);
  } catch {
    // not an absolute URL
    return false;
  }
}

function stripQueryAndHash(p) {
  return p.split('#')[0].split('?')[0];
}

function walk(dir) {
  const out = [];
  for (const ent of fs.readdirSync(dir, { withFileTypes: true })) {
    // Skip node_modules if someone adds it later
    if (ent.name === 'node_modules' || ent.name === '.git') continue;
    const full = path.join(dir, ent.name);
    if (ent.isDirectory()) out.push(...walk(full));
    else out.push(full);
  }
  return out;
}

function readText(file) {
  return fs.readFileSync(file, 'utf8');
}

function findRefs(html) {
  // intentionally simple: attribute extraction, not a full HTML parser
  const refs = [];
  const re = /\b(?:href|src)\s*=\s*(?:"([^"]+)"|'([^']+)'|([^\s>]+))/gim;
  let m;
  while ((m = re.exec(html))) {
    const raw = (m[1] ?? m[2] ?? m[3] ?? '').trim();
    if (raw) refs.push(raw);
  }
  return refs;
}

function resolveLocal(fromFile, rawRef) {
  const ref = stripQueryAndHash(rawRef);
  if (!ref) return null;
  if (isProbablyExternal(ref)) return null;

  // Root-relative paths in this static site map to repo root
  if (ref.startsWith('/')) return path.join(ROOT, ref.slice(1));

  // Otherwise resolve relative to the HTML file
  return path.resolve(path.dirname(fromFile), ref);
}

const htmlFiles = walk(ROOT).filter((f) => f.toLowerCase().endsWith('.html'));

const missing = [];
for (const file of htmlFiles) {
  const html = readText(file);
  for (const rawRef of findRefs(html)) {
    const target = resolveLocal(file, rawRef);
    if (!target) continue;

    // If it points at a directory, allow common index files
    if (fs.existsSync(target)) continue;
    if (fs.existsSync(target + '.html')) continue;

    const asDirIndex = path.join(target, 'index.html');
    if (fs.existsSync(asDirIndex)) continue;

    missing.push({ file: path.relative(ROOT, file), ref: rawRef });
  }
}

if (missing.length) {
  console.error(`\nBroken local refs found: ${missing.length}\n`);
  for (const m of missing) console.error(`- ${m.file} -> ${m.ref}`);
  console.error('\nTip: run from the site root:  node scripts/check-links.mjs\n');
  process.exit(1);
}

console.log(`OK: ${htmlFiles.length} HTML files scanned — no broken local refs found.`);

